import React, { useState, useRef } from 'react';
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card';
import CardColumns from 'react-bootstrap/CardColumns';
import Form from 'react-bootstrap/Form'

import './App.css';

function App() {

  return (
    <div className="App">
      <header className="App-header">
        <h1>People of Cepheus Light</h1>
        <h2>A Cepheus Light Character Generator</h2>
      </header>
      <Generator />
    </div>
  );
}

function Generator() {


  // basic info
  const [name, setName] = useState('Traveler');
  const [age, setAge] = useState('18');
  const [homeworld, setHomeworld] = useState('[HOMEWORLD]');

  // generator status
  interface Status {
    step: number,
    name: string,
  };

  const statuses: Status[] = [
    { step: 0, name: "Name" },
    { step: 1, name: "Characteristics", },
    { step: 2, name: "Homeworld Skills", },
    { step: 3, name: "Careers", },
    { step: 4, name: "Mustering Out", },
  ];

  const [status, setStatus] = useState(statuses[0]);

  // setting name
  const nameInput = useRef(name);
  const onNameSubmit = () => {
    console.log(nameInput.current);
    setName(nameInput.current);
  };

  return (
    <main>
      <section>
        <Card body>
          <Card.Title><h3>Step {status.step}: {status.name}</h3></Card.Title>
          <NameStep
            status={status}
            name={name}
            nameInput={nameInput}
            onNameSubmit={onNameSubmit}
          />
        </Card>
      </section>
      <section>
        <Card body>
          <Card.Title><h3>Character Data</h3></Card.Title>
          <CardColumns>
            <BasicInfoCard name={name} age={age} homeworld={homeworld} />
            <CharacteristicsCard />
            <SkillsCard />
            <CareerCard />
            <PossessionsCard />
          </CardColumns >
        </Card>
      </section>
      <section>
        <Card body>
          <Card.Title><h3>History</h3></Card.Title>
          <HistoryCard />
        </Card>
      </section>
    </main >
  )
}

function BasicInfoCard(props: any) {
  return (
    <Card body border='info'>
      <Card.Title>Basic Info</Card.Title>
      <Card.Text>
        <p><b>Name:</b> {props.name}</p>
        <p><b>Age:</b> {props.age}</p>
        <p><b>Homeworld:</b> {props.homeworld}</p>
      </Card.Text>
    </Card>
  );
}

function CharacteristicsCard(props: any) {
  return (
    <Card body border='info'>
      <Card.Title>Characteristics</Card.Title>
      <Card.Text>
        <p><b>STR:</b> [STR]</p>
        <p><b>DEX:</b> [DEX]</p>
        <p><b>END:</b> [END]</p>
        <p><b>INT:</b> [INT]</p>
        <p><b>EDU:</b> [EDU]</p>
        <p><b>SOC:</b> [SOC]</p>
      </Card.Text>
    </Card>
  );
}

function SkillsCard(props: any) {
  return (
    <Card body border='info'>
      <Card.Title>Skills</Card.Title>
      <Card.Text>
        <p><b>[SKILL 1]:</b> [LEVEL]</p>
        <p><b>[SKILL 2]:</b> [LEVEL]</p>
      </Card.Text>
    </Card>
  );
}

function CareerCard(props: any) {
  return (
    <Card body border='info'>
      <Card.Title>Career</Card.Title>
      <Card.Text>
        <p>[CAREER]</p>
        <p><b>Terms:</b> [TERMS]</p>
        <p><b>Rank:</b> [RANK]</p>
        <p><b>Retired:</b> [RETIRED]</p>
      </Card.Text>
    </Card>
  );
}

function HistoryCard(props: any) {
  return (
    <Card body border='secondary'>
      <Card.Text>
        Something happened.
      </Card.Text>
    </Card>
  );
}

function PossessionsCard(props: any) {
  return (
    <Card body border='info'>
      <Card.Title>Possessions</Card.Title>
      <Card.Text>
        <p><b>Funds:</b> [FUNDS]</p>
        <p><b>Item 1</b> x [QTY]</p>
        <p><b>Item 2</b> x [QTY]</p>
      </Card.Text>
    </Card>
  );
}

function NameStep(props: any) {
  if (props.status.step === 0) {
    return (
      <Card body border='primary'>
        <Form>
          <Form.Group>
            <Form.Label>Character Name</Form.Label>
            <Form.Control
              ref={props.nameInput}
              type="text"
            />
          </Form.Group>
          <Button onClick={props.onNameSubmit}>Submit</Button>
        </Form>
      </Card>
    );
  }
  return null;
}

export default App;
